package sprint3;import java.util.HashSet;import java.util.Set;public class CountNumbersInTheSentence{        public static int getUniqueWordsAmount( String text ){        if( text==null||text.isEmpty( ) ){                        return 0;        }        Set<String> uniqueWords=new HashSet<>( );        for( String word: text.split( " " ) ){            uniqueWords.add( word );        }                return uniqueWords.size( );    }}