package sprint3;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.chrome.ChromeDriver;import org.testng.Assert;import org.testng.annotations.Test;public class CountNumbersInTheSentenceTest{        @Test    public void testVerifyUniqueWordsAmount1(){                final String TEXT = "это мой дом не твой дом";                int expectedResult = CountNumbersInTheSentence.getUniqueWordsAmount(TEXT);                WebDriver driver = new ChromeDriver();        driver.get("https://planetcalc.ru/3204/");        driver.manage().window().maximize();                WebElement textArea = driver.findElement(                By.xpath("//textarea[@id='dialogv6411ef3aee88a_text']"));        textArea.clear();                textArea.sendKeys(TEXT);        driver.findElement(                By.xpath("//button[@id='dialogv6411ef3aee88a_calculate']")).click();                WebElement resultElement = driver.findElement(                By.xpath("//div[@class='pc-output-value p8' and @id='dialogv6411ef3aee88a_uniqueCount']"));        String resultText = resultElement.getText();                int actualResult = Integer.parseInt(resultText);                Assert.assertEquals(actualResult, expectedResult);                driver.quit();    }}